  "./themes/wireframe.css": "[data-theme=\"wireframe\"] {\n  color-scheme: light;\n  --color-base-100: oklch(100% 0 0);\n  --color-base-200: oklch(97% 0 0);\n  --color-base-300: oklch(93% 0 0);\n  --color-base-content: oklch(20% 0 0);\n  --color-primary: oklch(70% 0 0);\n  --color-primary-content: oklch(20% 0 0);\n  --color-secondary: oklch(80% 0 0);\n  --color-secondary-content: oklch(20% 0 0);\n  --color-accent: oklch(60% 0 0);\n  --color-accent-content: oklch(100% 0 0);\n  --color-neutral: oklch(40% 0 0);\n  --color-neutral-content: oklch(100% 0 0);\n  --color-info: oklch(70% 0.18 230);\n  --color-info-content: oklch(20% 0.05 230);\n  --color-success: oklch(70% 0.18 160);\n  --color-success-content: oklch(20% 0.05 160);\n  --color-warning: oklch(80% 0.15 85);\n  --color-warning-content: oklch(20% 0.05 85);\n  --color-error: oklch(65% 0.2 20);\n  --color-error-content: oklch(98% 0.02 20);\n  --radius-selector: 0.25rem;\n  --radius-field: 0.125rem;\n  --radius-box: 0.25rem;\n  --size-selector: 0.25rem;\n  --size-field: 0.25rem;\n  --border: 2px;\n  --depth: 0;\n  --noise: 0;\n}\n",
  "./themes/forest.css": "[data-theme=\"forest\"] {\n  color-scheme: dark;\n  --color-base-100: oklch(22% 0.03 150);\n  --color-base-200: oklch(19% 0.03 150);\n  --color-base-300: oklch(16% 0.03 150);\n  --color-base-content: oklch(90% 0.02 150);\n  --color-primary: oklch(55% 0.2 150);\n  --color-primary-content: oklch(98% 0.01 150);\n  --color-secondary: oklch(50% 0.15 100);\n  --color-secondary-content: oklch(98% 0.01 100);\n  --color-accent: oklch(70% 0.18 85);\n  --color-accent-content: oklch(20% 0.05 85);\n  --color-neutral: oklch(26% 0.025 150);\n  --color-neutral-content: oklch(88% 0.02 150);\n  --color-info: oklch(70% 0.18 230);\n  --color-info-content: oklch(20% 0.05 230);\n  --color-success: oklch(70% 0.18 160);\n  --color-success-content: oklch(20% 0.05 160);\n  --color-warning: oklch(80% 0.15 85);\n  --color-warning-content: oklch(20% 0.05 85);\n  --color-error: oklch(65% 0.2 20);\n  --color-error-content: oklch(98% 0.02 20);\n  --radius-selector: 0.5rem;\n  --radius-field: 0.25rem;\n  --radius-box: 0.5rem;\n  --size-selector: 0.25rem;\n  --size-field: 0.25rem;\n  --border: 1px;\n  --depth: 1;\n  --noise: 0;\n}\n",
  "./themes/theme.css": "/**\n * Default themes bundle \u2014 light + dark.\n *\n * light.css sets :root/:host defaults so pages work without data-theme.\n * The playground skips this file and imports all themes individually.\n *\n * For additional themes, import them in your input.css:\n *   @import \"./themes/dracula.css\";\n */\n\n/* Default theme (light) - sets :root, :host defaults */\n@import \"./light.css\";\n\n/* Dark theme */\n@import \"./dark.css\";\n",
  "./themes/cmyk.css": "[data-theme=\"cmyk\"] {\n  color-scheme: light;\n  --color-base-100: oklch(100% 0 0);\n  --color-base-200: oklch(96% 0 0);\n  --color-base-300: oklch(92% 0 0);\n  --color-base-content: oklch(20% 0 0);\n  --color-primary: oklch(70% 0.25 200);\n  --color-primary-content: oklch(98% 0.01 200);\n  --color-secondary: oklch(65% 0.3 330);\n  --color-secondary-content: oklch(98% 0.01 330);\n  --color-accent: oklch(85% 0.25 95);\n  --color-accent-content: oklch(20% 0.05 95);\n  --color-neutral: oklch(20% 0 0);\n  --color-neutral-content: oklch(95% 0 0);\n  --color-info: oklch(70% 0.2 230);\n  --color-info-content: oklch(20% 0.05 230);\n  --color-success: oklch(70% 0.2 160);\n  --color-success-content: oklch(20% 0.05 160);\n  --color-warning: oklch(80% 0.2 85);\n  --color-warning-content: oklch(20% 0.05 85);\n  --color-error: oklch(65% 0.25 20);\n  --color-error-content: oklch(98% 0.02 20);\n  --radius-selector: 0.5rem;\n  --radius-field: 0.25rem;\n  --radius-box: 0.5rem;\n  --size-selector: 0.25rem;\n  --size-field: 0.25rem;\n  --border: 1px;\n  --depth: 1;\n  --noise: 0;\n}\n",
